package Common;import java.time.LocalDate;import java.util.ArrayList;public class Extras {public static boolean Extras(String key){if(key.length()!=8) return false;int year=Integer.parseInt(key.substring(0, 4));int month=Integer.parseInt(key.substring(4,6));int day=Integer.parseInt(key.substring(6,8));String datetime=java.time.LocalDate.now() + "";String[] stringDate=(datetime.trim().split("-"));if (year > Integer.parseInt(stringDate[0]))return true;else {if(year==Integer.parseInt(stringDate[0])){if (month > Integer.parseInt(stringDate[1]))return true;else{if (month == Integer.parseInt(stringDate[1])){if (day >= Integer.parseInt(stringDate[2]))return true;else return false;}else return false;}}else return false;}}
}
